  let radius2 = makeRadius2();
  const spinParams = `${animationDuration} ${animationTimingFunction} ${animationDelay} ${animationIterationCount}`;

  return (
    <Shape
      size={size}
      radius1={radius1}
      radius2={radius2}
      spinParams={spinParams}
      {...props}
    >
      <ShapeContent spinParams={spinParams}>
